mapper.select:
SELECT
    SUM(agg.per_ticket) totalPerTicket,agg.wallet_address walletAddress FROM swarm_agg agg


        agg.date >= #{startDate} 


        and agg.date < #{endDate}


    and agg.wallet_address in

       #{item}



GROUP BY agg.wallet_address

SELECT SUM(a.ticket_valid) ticketValid from swarm_agg a LEFT JOIN swarm_node n on a.wallet_address = n.wallet_address
n.userId = #{userId}
and a.date >= #{beginYesterdayDate} AND #{endYesterdayDate} >= a.date
