SELECT
    SUM(agg.per_ticket_avail) totalPerTicketAvail,SUM(agg.per_ticket_valid) totalPerTicketValid,agg.peer_id peerId FROM swarm_one_hour_agg agg


        agg.date >= #{startDate} 


        and agg.date < #{endDate}


    and agg.peer_id in

       #{item}



GROUP BY agg.peer_id

SELECT
SUM(agg.link_num) totallinkNum,SUM(agg.ticket_valid) totalTicketValid,SUM(agg.convert_bzz) totalConvertBzz,agg.date FROM swarm_one_hour_agg agg


        agg.date >= #{startDate} 


        and agg.date < #{endDate}


        and agg.peer_id in

            #{item}



GROUP BY agg.date

SELECT SUM(a.per_ticket_valid) perTicketValid from swarm_one_hour_agg a LEFT JOIN swarm_node n on a.peer_id = n.peer_id
n.userId = #{userId}
and a.date >= #{beginYesterdayDate} AND #{endYesterdayDate} >= a.date
