mapper.select:
SELECT
SUM(agg.link_num) totallinkNum,SUM(agg.ticket_valid) totalTicketValid,SUM(agg.convert_bzz) totalConvertBzz,agg.date FROM swarm_one_hour_agg agg


        agg.date > #{startDate} 


        and agg.date <= #{endDate}


        and agg.peer_id in

            #{item}



GROUP BY agg.date

SELECT SUM(a.ticket_valid) ticketValid FROM swarm_one_day_agg a LEFT JOIN swarm_node n ON a.peer_id = n.peer_id
n.userId = #{userId}
and a.date = #{yesterDayDateYmd}
